package arrays;

import java.util.*;

public class Permutation {
    public boolean checkInclusion(String s1, String s2) {
        if(s1.length() > s2.length()) return false;
        var s1Hash = new HashMap<Character, Integer>();
        var s2Hash = new HashMap<Character, Integer>();
        for(int i=0; i<s1.length(); i++){
            if(!s1Hash.containsKey(s1.charAt(i)))s1Hash.put(s1.charAt(i), 1);
            else s1Hash.put(s1.charAt(i), s1Hash.get(s1.charAt(i)) + 1);
            if(!s2Hash.containsKey(s2.charAt(i)))s2Hash.put(s2.charAt(i), 1);
            else s2Hash.put(s2.charAt(i), s2Hash.get(s2.charAt(i)) + 1);
        }
        int i=0, j = s1.length()-1;
        while(i < s2.length() && j < s2.length()){
            System.out.println(i+" "+j);
            var flag = true;
            for(Map.Entry<Character, Integer> tempMap: s1Hash.entrySet()){
                if(!s2Hash.containsKey(tempMap.getKey())){
                    flag = false;
                    break;
                }else if(s2Hash.get(tempMap.getKey()) != tempMap.getValue()){
                    flag = false;
                    break;
                }
            }
            if(flag){
                return true;
            }
            s2Hash.put(s2.charAt(i), s2Hash.get(s2.charAt(i)) - 1);
            i++;
            j++;
            if(j < s2.length() && !s2Hash.containsKey(s2.charAt(j)))s2Hash.put(s2.charAt(j), 1);
            else if(j < s2.length()) s2Hash.put(s2.charAt(j), s2Hash.get(s2.charAt(j))+1);
        }
        return false;
    }

    public static void main(String[] args){
        var s1 = "";
        var s2 = "";
        Permutation p = new Permutation();
        p.checkInclusion(s1, s2);
    }
}
